class BookDjangoSource(BookDbSource):
    """
    multiple Django table as data source
    """

    def __init__(self, models, **keywords):
        self.__models = models
        BookDbSource.__init__(self, DB_DJANGO, **keywords)

    def get_params(self):
        return self.__models
